      type: [Number, String],
      default: "0 0 60 55"
    },
    iconColor: {
      type: String,
      default: 'white'
    },
    strokeColor: {
      type: String,
      default: '#BEBEBE'
    }
  },
  data() {
    return {
      activeTab: 'home'
    };
  },
  watch: {
    $route(to, from) {
      this.getActiveTab();
    }
  },
  methods: {
    isActiveTab(tabName) {
      if (this.activeTab === tabName) {
        return {
          fill: "#F53B57"
        };
      }
      return {
        fill: "#BEBEBE"
      };
    },
    getActiveTab() {
      /* STUDENTS */
      if (this.$route.path.includes('teacher/students') || this.$route.path.includes('teacher/student') || this.$route.path.includes('teacher/student') || this.$route.path.includes('students/profile')) {
        this.activeTab = 'students';
      }

      /* CLASS */else if (this.$route.path.includes('teacher/classes') || this.$route.path.includes('teacher/class') || this.$route.path.includes('students/class')) {
        this.activeTab = 'classes';
      }

      /* ASSIGNMENTS */else if (this.$route.path.includes('teacher/assignments') || this.$route.path.includes('teacher/assignment') || this.$route.path.includes('students/assignments')) {
        this.activeTab = 'assignments';
      }

      /* */else if (this.$route.path.includes('/settings')) {
        this.activeTab = 'settings';
      } else {
        this.activeTab = 'home';
      }
    }
  },
  created() {
    this.getActiveTab();
  },
  components: {
    IconBase
  }
};
