import VueMarkdown from 'vue-markdown';
export default {
  name: "TextMultilineTruncate",
  props: {
    text: String,
    lines: {
      type: Number,
      default: 2
    }
  },
  computed: {
    style() {
      let value = '';
      value += ' display: -webkit-box;';
      value += ' -webkit-box-orient: vertical;';
      value += ' overflow: hidden;';
      value += ` -webkit-line-clamp: ${this.lines};`;
      return value;
    }
  },
  components: {
    VueMarkdown
  }
};
